import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function App() {
  // MATCH STATE
  const [innings, setInnings] = useState(1);
  const [totalOvers, setTotalOvers] = useState(5);
  const [matchStarted, setMatchStarted] = useState(false);

  // DARK / LIGHT MODE
  const [dark, setDark] = useState(false);

  // SCORE STATE
  const [runs, setRuns] = useState(0);
  const [wickets, setWickets] = useState(0);
  const [balls, setBalls] = useState(0);

  // HISTORY
  const [ballHistory, setBallHistory] = useState([]);
  const [overHistory, setOverHistory] = useState([]);

  const [matchResult, setMatchResult] = useState("");

  const maxBalls = totalOvers * 6;
  const overs = `${Math.floor(balls / 6)}.${balls % 6}`;
  const currentOverIndex = Math.floor(balls / 6);

  const ensureOverExists = () => {
    if (!overHistory[currentOverIndex]) {
      setOverHistory(prev => [
        ...prev,
        { over: currentOverIndex + 1, runs: 0, balls: [] }
      ]);
    }
  };

  const recordBall = (action, run, isLegalBall = true) => {
    ensureOverExists();
    setOverHistory(prev => {
      const updated = [...prev];
      updated[currentOverIndex].runs += run;
      updated[currentOverIndex].balls.push(action);
      return updated;
    });

    setBallHistory(prev => [...prev, { action, run, isLegalBall }]);
  };

  const addRun = (r) => {
    if (balls >= maxBalls || matchResult) return;
    setRuns(runs + r);
    setBalls(balls + 1);
    recordBall(String(r), r, true);
  };

  const addExtra = (type, r) => {
    if (matchResult) return;
    setRuns(runs + r);
    recordBall(`${type}+${r}`, r, false);
  };

  const fallWicket = () => {
    if (balls >= maxBalls || matchResult) return;
    setWickets(wickets + 1);
    setBalls(balls + 1);
    recordBall("W", 0, true);
  };

  // FIXED UNDO SYSTEM
  const undo = () => {
    if (ballHistory.length === 0) return;

    const last = ballHistory[ballHistory.length - 1];
    const newHistory = ballHistory.slice(0, -1);
    setBallHistory(newHistory);

    setRuns(runs - last.run);
    if (last.action === "W") setWickets(wickets - 1);

    if (last.isLegalBall) setBalls(balls - 1);

    setOverHistory(prev => {
      const updated = [...prev];
      let idx = updated.length - 1;

      while (idx >= 0 && updated[idx].balls.length === 0) idx--;

      if (idx >= 0) {
        updated[idx].balls.pop();
        updated[idx].runs -= last.run;

        if (updated[idx].balls.length === 0 && updated[idx].runs === 0 && idx > 0) {
          updated.splice(idx, 1);
        }
      }
      return updated;
    });
  };

  // INNINGS STATE
  const [target, setTarget] = useState(null);
  const [innings1Score, setInnings1Score] = useState({ runs: null, wickets: null, overs: null });
  const [innings2Score, setInnings2Score] = useState({ runs: null, wickets: null, overs: null });

  const endInnings = () => {
    if (innings === 1) {
      setInnings1Score({ runs, wickets, overs });
      setTarget(runs + 1);

      setInnings(2);
      setRuns(0);
      setWickets(0);
      setBalls(0);
      setBallHistory([]);
      setOverHistory([]);
    } else {
      setInnings2Score({ runs, wickets, overs });

      let result = "";
      if (runs >= target) {
        result = `Team B WON by ${10 - wickets} wickets`;
      } else if (runs === target - 1) {
        result = "Match TIED";
      } else {
        result = `Team A WON by ${target - runs - 1} runs`;
      }
      setMatchResult(result);
    }
  };

  // AUTO WIN DETECTION
  useEffect(() => {
    if (innings === 2 && target !== null) {
      if (runs >= target) {
        setInnings2Score({ runs, wickets, overs });
        setMatchResult(`Team B WON by ${10 - wickets} wickets`);
      }
      if (balls === maxBalls && runs < target) {
        setInnings2Score({ runs, wickets, overs });
        setMatchResult(`Team A WON by ${target - runs - 1} runs`);
      }
    }
  }, [runs, balls]);

  const resetMatch = () => {
    setRuns(0);
    setWickets(0);
    setBalls(0);
    setBallHistory([]);
    setOverHistory([]);
    setMatchStarted(false);
    setTarget(null);
    setMatchResult("");
    setInnings(1);
    setInnings1Score({ runs: null, wickets: null, overs: null });
    setInnings2Score({ runs: null, wickets: null, overs: null });
  };

  return (
    <div className={dark ? "bg-dark text-white min-vh-100 p-4" : "bg-light text-dark min-vh-100 p-4"}>
      <div className="container">

        {/* DARK MODE */}
        <button className="btn btn-outline-info float-end mb-3" onClick={() => setDark(!dark)}>
          {dark ? "Light Mode" : "Dark Mode"}
        </button>

        {/* SCOREBOARD SUMMARY */}
        {(innings1Score.runs !== null || innings2Score.runs !== null) && (
          <div className="card p-3 shadow mb-3">
            <h4 className="fw-bold">Match Summary</h4>
            <table className="table table-bordered mt-2 text-center">
              <thead>
                <tr>
                  <th>Innings</th>
                  <th>Score</th>
                  <th>Overs</th>
                </tr>
              </thead>
              <tbody>
                {innings1Score.runs !== null && (
                  <tr>
                    <td>1st</td>
                    <td>{innings1Score.runs}/{innings1Score.wickets}</td>
                    <td>{innings1Score.overs}</td>
                  </tr>
                )}
                {innings2Score.runs !== null && (
                  <tr>
                    <td>2nd</td>
                    <td>{innings2Score.runs}/{innings2Score.wickets}</td>
                    <td>{innings2Score.overs}</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* SET OVERS */}
        {!matchStarted && (
          <div className="card p-4 shadow text-center">
            <h3>Select Total Overs</h3>
            <input
              type="number"
              className="form-control w-50 mx-auto mb-3"
              min="1"
              value={totalOvers}
              onChange={(e) => setTotalOvers(Number(e.target.value))}
            />
            <button className="btn btn-success" onClick={() => setMatchStarted(true)}>
              Start Match
            </button>
          </div>
        )}

        {/* MAIN MATCH UI */}
        {matchStarted && !matchResult && (
          <>
            <h2 className="fw-bold mt-3">
              {innings === 1 ? "Innings 1" : `Innings 2 (Target: ${target})`}
            </h2>

            <div className="card p-4 text-center shadow">
              <h1>{runs} / {wickets}</h1>
              <h4>Overs: {overs} / {totalOvers}</h4>

              {innings === 2 && (
                <div className="alert alert-warning fw-bold mt-3">
                  Need {target - runs} runs in {maxBalls - balls} balls
                </div>
              )}
            </div>

            {/* RUN BUTTONS */}
            <div className="text-center mt-3">
              <div className="btn-group">
                {[0, 1, 2, 3, 4, 6].map(n => (
                  <button key={n} className="btn btn-primary mx-1" onClick={() => addRun(n)}>
                    {n}
                  </button>
                ))}
                <button className="btn btn-dark mx-1" onClick={() => setRuns(Math.max(0, runs - 1))}>
                  -1
                </button>
              </div>

              <div className="btn-group mt-3">
                <button className="btn btn-warning mx-1" onClick={() => addExtra("Wd", 1)}>Wide +1</button>
                <button className="btn btn-warning mx-1" onClick={() => addExtra("NB", 1)}>No Ball +1</button>
              </div>

              <div className="mt-3">
                <button className="btn btn-danger mx-1" onClick={fallWicket}>WICKET</button>
                <button className="btn btn-secondary mx-1" onClick={undo}>Undo</button>
                <button className="btn btn-success mx-1" onClick={endInnings}>
                  {innings === 1 ? "End 1st Innings" : "Finish Match"}
                </button>
              </div>
            </div>

            {/* OVER SUMMARY */}
            <h3 className="mt-4">Over Summary</h3>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Over</th>
                  <th>Runs</th>
                  <th>Balls</th>
                </tr>
              </thead>
              <tbody>
                {overHistory.map((over, idx) => (
                  <tr key={idx}>
                    <td>{over.over}</td>
                    <td>{over.runs}</td>
                    <td>{over.balls.join(" , ")}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {/* WINNER SCREEN */}
        {matchResult && (
          <div className="text-center py-5">
            <div className="p-4 rounded-circle bg-success bg-opacity-25 d-inline-block mb-3">
              <svg width="90" height="90" fill="green">
                <circle cx="45" cy="45" r="40" stroke="green" strokeWidth="5" fill="none"/>
                <path d="M25 45 L40 60 L70 30" stroke="green" strokeWidth="6" fill="none"/>
              </svg>
            </div>

            <h1 className="fw-bold text-success">{matchResult}</h1>
            <p className="text-muted fs-5">Match Completed</p>

            <button className="btn btn-primary btn-lg mt-3" onClick={resetMatch}>
              Start New Match
            </button>
          </div>
        )}

      </div>
    </div>
  );
}
