import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function App() {
  const [runs, setRuns] = useState(0);
  const [wickets, setWickets] = useState(0);
  const [balls, setBalls] = useState(0); // store total balls bowled

  // Convert balls → overs format (e.g., 14 balls = 2.2 overs)
  const overs = `${Math.floor(balls / 6)}.${balls % 6}`;

  const addRun = (r) => {
    setRuns(runs + r);
    addBall();
  };

  const addBall = () => {
    setBalls(prev => prev + 1);
  };

  const addExtra = (r) => {
    // No ball, wide → no ball count
    setRuns(runs + r);
  };

  const fallWicket = () => {
    setWickets(wickets + 1);
    addBall();
  };

  const resetMatch = () => {
    setRuns(0);
    setWickets(0);
    setBalls(0);
  };

  return (
    <div className="container p-4 text-center">
      <h1 className="mb-4 fw-bold">Cricket Score Calculator</h1>

      <div className="card shadow p-4 mb-4">
        <h2 className="display-5">{runs} / {wickets}</h2>
        <p className="fs-4">Overs: {overs}</p>
      </div>

      <h4 className="mb-2">Add / Remove Runs</h4>
      <div className="btn-group mb-4">

        {/* -1 button */}
        <button 
          className="btn btn-dark mx-1"
          onClick={() => setRuns(runs > 0 ? runs - 1 : 0)}
        >
          -1
        </button>

        {/* 1,2,3,4,6 buttons with ball count */}
        {[1,2,3,4,6].map(n => (
          <button 
            key={n}
            className="btn btn-primary mx-1"
            onClick={() => addRun(n)}
          >
            +{n}
          </button>
        ))}
      </div>

      <h4 className="mb-2">Extras</h4>
      <div className="btn-group mb-4">
        <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>Wide +1</button>
        <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>No Ball +1</button>
      </div>

      <div className="mb-4">
        <button className="btn btn-danger btn-lg" onClick={fallWicket}>Wicket</button>
      </div>

      <button className="btn btn-secondary" onClick={resetMatch}>Reset Match</button>
    </div>
  );
}
