import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function App() {
  // MATCH STATE
  const [innings, setInnings] = useState(1); // 1 or 2
  const [totalOvers, setTotalOvers] = useState(5);
  const [matchStarted, setMatchStarted] = useState(false);

  // SCORE STATE
  const [runs, setRuns] = useState(0);
  const [wickets, setWickets] = useState(0);
  const [balls, setBalls] = useState(0);

  // HISTORY
  const [ballHistory, setBallHistory] = useState([]); // single innings
  const [overHistory, setOverHistory] = useState([]); // [{over, runs, balls:[] }]
  
  const [matchResult, setMatchResult] = useState("");

  const maxBalls = totalOvers * 6;
  const overs = `${Math.floor(balls / 6)}.${balls % 6}`;

  // Convert current ball into over index
  const currentOverIndex = Math.floor(balls / 6);

  const ensureOverExists = () => {
    if (!overHistory[currentOverIndex]) {
      setOverHistory(prev => [
        ...prev,
        { over: currentOverIndex + 1, runs: 0, balls: [] }
      ]);
    }
  };

  const recordBall = (action, run) => {
    ensureOverExists();
    setOverHistory(prev => {
      const updated = [...prev];
      updated[currentOverIndex].runs += run;
      updated[currentOverIndex].balls.push(action);
      return updated;
    });

    setBallHistory([...ballHistory, { action, run, over: overs }]);
  };

  const addRun = (r) => {
    if (balls >= maxBalls) return endInnings();

    setRuns(runs + r);
    setBalls(balls + 1);
    recordBall(`+${r}`, r);
  };

  const addExtra = (r) => {
    setRuns(runs + r);
    recordBall("Extra +" + r, r);
  };

  const fallWicket = () => {
    if (balls >= maxBalls) return endInnings();

    setWickets(wickets + 1);
    setBalls(balls + 1);
    recordBall("Wk", 0);
  };

  const undo = () => {
    if (ballHistory.length === 0) return;

    const last = ballHistory[ballHistory.length - 1];
    setBallHistory(ballHistory.slice(0, -1));

    const overIdx = currentOverIndex;

    setOverHistory(prev => {
      const updated = [...prev];
      if (updated[overIdx]) {
        updated[overIdx].runs -= last.run;
        updated[overIdx].balls.pop();
      }
      return updated;
    });

    if (last.action === "Wk") {
      setWickets(wickets - 1);
      setBalls(balls - 1);
    } else {
      setRuns(runs - last.run);
      if (!last.action.startsWith("Extra")) {
        setBalls(balls - 1);
      }
    }
  };

  // END INNINGS SYSTEM
  const [target, setTarget] = useState(null);
  const [teamOneScore, setTeamOneScore] = useState(null);

  const endInnings = () => {
    if (innings === 1) {
      setTeamOneScore(runs);
      setTarget(runs + 1);

      // PREPARE FOR SECOND INNINGS
      setInnings(2);
      setRuns(0);
      setWickets(0);
      setBalls(0);
      setBallHistory([]);
      setOverHistory([]);
    } else {
      // MATCH FINISH
      let result = "";

      if (runs >= target) {
        result = `Team B WON by ${10 - wickets} wickets`;
      } else {
        result = `Team A WON by ${target - runs - 1} runs`;
      }

      setMatchResult(result);
    }
  };

  const finishMatch = () => endInnings();

  const resetMatch = () => {
    setRuns(0);
    setWickets(0);
    setBalls(0);
    setBallHistory([]);
    setOverHistory([]);
    setMatchStarted(false);
    setInnings(1);
    setTarget(null);
    setMatchResult("");
  };

  return (
    <div className="container p-4">

      {!matchStarted && (
        <div className="card p-4 shadow text-center">
          <h3>Select Total Overs</h3>
          <input
            type="number"
            className="form-control mb-3"
            value={totalOvers}
            min="1"
            onChange={e => setTotalOvers(Number(e.target.value))}
          />
          <button className="btn btn-success" onClick={() => setMatchStarted(true)}>
            Start Match
          </button>
        </div>
      )}

      {matchStarted && (
        <>
          <h2 className="fw-bold mb-3">
            {innings === 1 ? "Innings 1" : "Innings 2 (Target: " + target + ")"}
          </h2>

          <div className="card p-4 text-center shadow">
            <h1>{runs} / {wickets}</h1>
            <h4>Overs: {overs} / {totalOvers}</h4>
          </div>

          <div className="text-center mt-4">
            <div className="btn-group">
              {[1, 2, 3, 4, 6].map(n => (
                <button key={n} className="btn btn-primary mx-1" onClick={() => addRun(n)}>
                  +{n}
                </button>
              ))}
              <button className="btn btn-dark mx-1" onClick={() => setRuns(runs - 1 >= 0 ? runs - 1 : 0)}>
                -1
              </button>
            </div>

            <div className="btn-group mt-3">
              <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>Wide +1</button>
              <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>No Ball +1</button>
            </div>

            <div className="mt-3">
              <button className="btn btn-danger mx-1" onClick={fallWicket}>Wicket</button>
              <button className="btn btn-secondary mx-1" onClick={undo}>Undo</button>
              <button className="btn btn-success mx-1" onClick={finishMatch}>
                {innings === 1 ? "End Innings 1" : "Finish Match"}
              </button>
            </div>
          </div>

          {/* OVER SUMMARY */}
          <h3 className="mt-4">Per Over Summary</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Over</th>
                <th>Runs</th>
                <th>Balls</th>
              </tr>
            </thead>
            <tbody>
              {overHistory.map((over, idx) => (
                <tr key={idx}>
                  <td>{over.over}</td>
                  <td>{over.runs}</td>
                  <td>{over.balls.join(" , ")}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {matchResult && (
            <div className="alert alert-info text-center fs-3 fw-bold mt-4">
              {matchResult}
            </div>
          )}

          <div className="text-center mt-3">
            <button className="btn btn-outline-secondary" onClick={resetMatch}>Reset Match</button>
          </div>
        </>
      )}
    </div>
  );
}
