import { useState, useEffect } from "react";
import { Trophy, RotateCcw, Moon, Sun, Circle, Activity } from "lucide-react";

export default function App() {
  // MATCH STATE
  const [innings, setInnings] = useState(1);
  const [totalOvers, setTotalOvers] = useState(5);
  const [matchStarted, setMatchStarted] = useState(false);

  // DARK / LIGHT MODE
  const [dark, setDark] = useState(false);

  // SCORE STATE
  const [runs, setRuns] = useState(0);
  const [wickets, setWickets] = useState(0);
  const [balls, setBalls] = useState(0);

  // HISTORY
  const [ballHistory, setBallHistory] = useState([]);
  const [overHistory, setOverHistory] = useState([]);
  const [matchResult, setMatchResult] = useState("");

  // INNINGS STATE
  const [target, setTarget] = useState(null);
  const [innings1Score, setInnings1Score] = useState({ runs: null, wickets: null, overs: null });
  const [innings2Score, setInnings2Score] = useState({ runs: null, wickets: null, overs: null });

  const maxBalls = totalOvers * 6;
  
  // Display logic for overs (e.g., 0.5 then 1.0)
  const displayOvers = `${Math.floor(balls / 6)}.${balls % 6}`;
  
  // Current over index (0 for 1st over, 1 for 2nd over)
  const currentOverIndex = Math.floor(balls / 6);

  // Helper to safely record a ball in history
  // We pass 'currentBalls' explicitly to avoid stale state closures
  const recordBall = (action, run, isLegalBall = true) => {
    const curIndex = Math.floor(balls / 6);
    
    setOverHistory(prev => {
      const updated = [...prev];
      
      // Safety check: Create over object if it doesn't exist
      if (!updated[curIndex]) {
        updated[curIndex] = { over: curIndex + 1, runs: 0, balls: [] };
      }

      updated[curIndex].runs += run;
      updated[curIndex].balls.push(action);
      return updated;
    });

    setBallHistory(prev => [...prev, { action, run, isLegalBall }]);
  };

  const addRun = (r) => {
    if (balls >= maxBalls) return endInnings();
    
    setRuns(prev => prev + r);
    setBalls(prev => prev + 1);
    recordBall(r === 0 ? "0" : r, r, true);
  };

  const addExtra = (type, r) => {
    // Extras (Wide/No Ball) add runs but do NOT consume a ball
    if (matchResult) return;
    setRuns(prev => prev + r);
    // We do not increment 'balls' state here
    recordBall(`${type} +${r}`, r, false);
  };

  const fallWicket = () => {
    if (balls >= maxBalls) return endInnings();
    
    setWickets(prev => prev + 1);
    setBalls(prev => prev + 1);
    recordBall("W", 0, true);
  };

  const undo = () => {
    if (ballHistory.length === 0) return;

    const last = ballHistory[ballHistory.length - 1];
    const newHistory = ballHistory.slice(0, -1);
    setBallHistory(newHistory);

    // Reverse specific actions
    setRuns(prev => prev - last.run);
    if (last.action === "W") {
      setWickets(prev => prev - 1);
    }
    
    if (last.isLegalBall) {
      setBalls(prev => prev - 1);
    }

    // Update Over History
    // We need to find where this ball lived. 
    // If it was legal, it was at the end of the array.
    // If it was extra, it was also at the end.
    // Determining the correct over index for the undo involves checking if we just stepped back an over.
    
    setOverHistory(prev => {
      const updated = [...prev];
      // Determine which over index to modify. 
      // If we just undid a legal ball that was the first of a new over, we need to look at previous over?
      // Actually, since we pushed to the END of the active over array, we just need to pop from the last active over.
      
      // Find the last over that has balls
      let lastActiveIndex = updated.length - 1;
      while(lastActiveIndex >= 0 && updated[lastActiveIndex].balls.length === 0) {
         lastActiveIndex--;
      }

      if (lastActiveIndex >= 0) {
        updated[lastActiveIndex].runs -= last.run;
        updated[lastActiveIndex].balls.pop();
        
        // Cleanup empty overs if needed, though keeping structure is usually safer
        if (updated[lastActiveIndex].balls.length === 0 && updated[lastActiveIndex].runs === 0 && lastActiveIndex > 0) {
             // Optional: could remove the over object if it's empty and not the first one
        }
      }
      return updated;
    });
  };

  const endInnings = () => {
    if (innings === 1) {
      setInnings1Score({ runs, wickets, overs: displayOvers });
      setTarget(runs + 1);

      // Switch to inning 2
      setInnings(2);
      setRuns(0);
      setWickets(0);
      setBalls(0);
      setBallHistory([]);
      setOverHistory([]);
    } else {
      // Save final score
      setInnings2Score({ runs, wickets, overs: displayOvers });

      // Result logic
      let result = "";
      if (runs >= target) {
        result = `Team B WON by ${10 - wickets} wickets`;
      } else {
        result = `Team A WON by ${target - runs - 1} runs`;
      }
      
      // Handle Tie
      if(runs === target - 1) {
          result = "Match TIED";
      }
      
      setMatchResult(result);
    }
  };

  const resetMatch = () => {
    setRuns(0);
    setWickets(0);
    setBalls(0);
    setBallHistory([]);
    setOverHistory([]);
    setMatchStarted(false);
    setTarget(null);
    setMatchResult("");
    setInnings(1);
    setInnings1Score({ runs: null, wickets: null, overs: null });
    setInnings2Score({ runs: null, wickets: null, overs: null });
  };

  // Auto-check for win condition in 2nd innings
  useEffect(() => {
    if (innings === 2 && target && runs >= target) {
        endInnings();
    }
    if (innings === 2 && balls === maxBalls && runs < target) {
        endInnings();
    }
  }, [runs, balls, innings, target]);

  // Theme Classes
  const bgClass = dark ? "bg-slate-900 text-white" : "bg-gray-100 text-gray-900";
  const cardClass = dark ? "bg-slate-800 shadow-lg border-slate-700" : "bg-white shadow-lg border-gray-200";
  const btnBase = "px-4 py-2 rounded-lg font-semibold transition-all active:scale-95";

  return (
    <div className={`min-h-screen transition-colors duration-300 p-4 ${bgClass}`}>
      <div className="max-w-2xl mx-auto">

        {/* HEADER */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold flex items-center gap-2">
            <Activity className="text-blue-500" /> CricScore
          </h1>
          <button
            className={`p-2 rounded-full ${dark ? "bg-slate-700 hover:bg-slate-600" : "bg-gray-200 hover:bg-gray-300"}`}
            onClick={() => setDark(!dark)}
          >
            {dark ? <Sun size={20} /> : <Moon size={20} />}
          </button>
        </div>

        {/* SCOREBOARD SUMMARY */}
        {(innings1Score.runs !== null || innings2Score.runs !== null) && (
          <div className={`${cardClass} rounded-xl p-4 mb-6`}>
            <h4 className="font-bold border-b pb-2 mb-2 border-gray-300/20 flex items-center gap-2">
               <Trophy size={18} className="text-yellow-500"/> Match Summary
            </h4>
            <div className="grid grid-cols-3 gap-4 text-center text-sm">
              <div className="font-bold text-gray-500 uppercase tracking-wider">Inn</div>
              <div className="font-bold text-gray-500 uppercase tracking-wider">Score</div>
              <div className="font-bold text-gray-500 uppercase tracking-wider">Ov</div>
              
              {innings1Score.runs !== null && (
                <>
                  <div className="py-1">1st Inn</div>
                  <div className="py-1 font-mono">{innings1Score.runs}/{innings1Score.wickets}</div>
                  <div className="py-1 font-mono">{innings1Score.overs}</div>
                </>
              )}
              {innings2Score.runs !== null && (
                <>
                  <div className="py-1">2nd Inn</div>
                  <div className="py-1 font-mono">{innings2Score.runs}/{innings2Score.wickets}</div>
                  <div className="py-1 font-mono">{innings2Score.overs}</div>
                </>
              )}
            </div>
          </div>
        )}

        {/* START SCREEN */}
        {!matchStarted && (
          <div className={`${cardClass} rounded-xl p-8 text-center`}>
            <h3 className="text-xl font-bold mb-4">New Match Settings</h3>
            <div className="mb-6">
              <label className="block text-sm font-medium mb-2 opacity-80">Total Overs</label>
              <input
                type="number"
                className={`w-32 text-center text-xl p-2 rounded border ${dark ? "bg-slate-900 border-slate-600" : "bg-white border-gray-300"}`}
                min="1"
                value={totalOvers}
                onChange={(e) => setTotalOvers(Number(e.target.value))}
              />
            </div>
            <button 
                className={`${btnBase} bg-green-600 text-white hover:bg-green-700 w-full max-w-xs`} 
                onClick={() => setMatchStarted(true)}
            >
              Start Match
            </button>
          </div>
        )}

        {/* MAIN MATCH UI */}
        {matchStarted && !matchResult && (
          <>
            <div className="flex justify-between items-end mb-2 px-2">
               <span className="font-bold opacity-70">
                 {innings === 1 ? "Innings 1" : "Innings 2"}
               </span>
               {innings === 2 && <span className="text-sm font-mono bg-blue-100 text-blue-800 px-2 py-1 rounded">Target: {target}</span>}
            </div>

            {/* MAIN SCORE CARD */}
            <div className={`${cardClass} rounded-2xl p-6 text-center mb-6 relative overflow-hidden`}>
               <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-blue-500 to-purple-500"></div>
              <h1 className="text-6xl font-black tracking-tighter mb-2">
                {runs}<span className="text-3xl text-gray-400">/{wickets}</span>
              </h1>
              <div className="text-xl opacity-80 font-mono">
                Overs: {displayOvers} <span className="text-sm opacity-60">/ {totalOvers}</span>
              </div>
              
              {/* Req run rate if 2nd innings */}
              {innings === 2 && (
                  <div className="mt-4 text-sm font-medium text-orange-500">
                      Need {target - runs} runs from {maxBalls - balls} balls
                  </div>
              )}
            </div>

            {/* CONTROLS */}
            <div className="grid gap-4">
                {/* Runs */}
                <div className="grid grid-cols-5 gap-2">
                    {[0, 1, 2, 3, 4, 6].map(n => (
                    <button 
                        key={n} 
                        className={`${btnBase} ${dark ? 'bg-slate-700 hover:bg-slate-600' : 'bg-white border border-gray-200 hover:bg-gray-50'}`}
                        onClick={() => addRun(n)}
                    >
                        {n}
                    </button>
                    ))}
                </div>

                {/* Extras & Wickets */}
                <div className="grid grid-cols-3 gap-2">
                    <button className={`${btnBase} bg-yellow-600 text-white hover:bg-yellow-700`} onClick={() => addExtra("Wd", 1)}>wd</button>
                    <button className={`${btnBase} bg-yellow-600 text-white hover:bg-yellow-700`} onClick={() => addExtra("NB", 1)}>nb</button>
                    <button className={`${btnBase} bg-red-600 text-white hover:bg-red-700`} onClick={fallWicket}>OUT</button>
                </div>
                
                <div className="grid grid-cols-2 gap-2">
                     <button className={`${btnBase} bg-gray-500 text-white hover:bg-gray-600 flex justify-center items-center gap-2`} onClick={undo}>
                        <RotateCcw size={16}/> Undo
                     </button>
                     <button className={`${btnBase} bg-blue-600 text-white hover:bg-blue-700`} onClick={endInnings}>
                        {innings === 1 ? "End Innings" : "Finish"}
                     </button>
                </div>
            </div>

            {/* CURRENT OVER STATS */}
            <div className="mt-8">
                <h3 className="text-sm font-bold uppercase tracking-wider opacity-60 mb-3">Recent Overs</h3>
                <div className="space-y-3">
                    {[...overHistory].reverse().map((over, idx) => (
                    <div key={idx} className={`flex items-center justify-between p-3 rounded-lg ${dark ? 'bg-slate-800' : 'bg-white'} border ${dark ? 'border-slate-700' : 'border-gray-100'}`}>
                        <div className="flex items-center gap-3">
                            <span className="font-mono text-xs opacity-50">Ov {over.over}</span>
                            <div className="flex gap-1">
                                {over.balls.map((b, i) => (
                                    <span key={i} className={`
                                        w-6 h-6 flex items-center justify-center rounded-full text-xs font-bold
                                        ${b === 'W' ? 'bg-red-100 text-red-700' : 
                                          b === 4 || b === 6 ? 'bg-green-100 text-green-700' : 
                                          'bg-gray-100 text-gray-700'}
                                    `}>
                                        {b}
                                    </span>
                                ))}
                            </div>
                        </div>
                        <span className="font-bold text-sm">{over.runs} runs</span>
                    </div>
                    ))}
                    {overHistory.length === 0 && <div className="text-center opacity-40 text-sm py-4">No overs bowled yet</div>}
                </div>
            </div>
          </>
        )}

        {/* RESULT SCREEN */}
        {matchResult && (
            <div className="text-center py-8 animate-fade-in">
                <div className="inline-block p-4 bg-green-100 text-green-800 rounded-full mb-4">
                    <Trophy size={48} />
                </div>
                <h2 className="text-3xl font-black mb-2">{matchResult}</h2>
                <p className="opacity-60 mb-8">Match Completed</p>
                <button className={`${btnBase} bg-blue-600 text-white hover:bg-blue-700 px-8`} onClick={resetMatch}>
                    Start New Match
                </button>
            </div>
        )}

      </div>
    </div>
  );
}
