    const [textBox1Value, setTextBox1Value] = useState('');
    const [pic, setPic]=useState(0)


    useEffect(()=>{
    if (textBox1Value==="cat")
 setPic(cat)
else if (textBox1Value==="lion")
setPic(Lion)
else if (textBox1Value==="god")
 setPic(dog)
 else if (textBox1Value==="don")
 setPic(don)
})

