import "./App.css";
import { useState } from "react";

function App() {
  const [x, setX] = useState("");
  const [y, setY] = useState("");

  // Convert inputs safely
  const num1 = Number(x) || 0;
  const num2 = Number(y) || 0;

  return (
    <>
      <div className="calculator">
        <div className="display">
          <input
            type="number"
            placeholder="Enter first number"
            value={x}
            onChange={(e) => setX(e.target.value)}
          />
          <input
            type="number"
            placeholder="Enter second number"
            value={y}
            onChange={(e) => setY(e.target.value)}
          />
        </div>

        <div id="a3">Addition: {num1 + num2}</div>
        <div id="a4">Subtraction: {num1 - num2}</div>
        <div id="a5">Multiplication: {num1 * num2}</div>
        <div id="a6">
          Division: {num2 !== 0 ? (num1 / num2).toFixed(2) : "Error (รท0)"}
        </div>
      </div>
    </>
  );
}

export default App;
