import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default function App() {
  const [runs, setRuns] = useState(0);
  const [wickets, setWickets] = useState(0);
  const [balls, setBalls] = useState(0);
  const [history, setHistory] = useState([]); // stores ball-by-ball
  const [dark, setDark] = useState(false);
  const [totalOvers, setTotalOvers] = useState(5);
  const [matchStarted, setMatchStarted] = useState(false);
  const maxBalls = totalOvers * 6;

  // Convert balls → overs format
  const overs = `${Math.floor(balls / 6)}.${balls % 6}`;

  // Add ball event
  const recordBall = (action, run) => {
    const newEvent = { action, run, over: overs };
    setHistory([...history, newEvent]);
  };

  // Add run with ball count
  const addRun = (r) => {
    if (balls >= maxBalls) return alert("Match over!");

    setRuns(runs + r);
    setBalls(balls + 1);
    recordBall(`+${r}`, r);
  };

  // Extras (no ball, wide)
  const addExtra = (r) => {
    setRuns(runs + r);
    recordBall("Extra +" + r, r);
  };

  // Wicket event
  const fallWicket = () => {
    if (balls >= maxBalls) return alert("Match over!");

    setWickets(wickets + 1);
    setBalls(balls + 1);
    recordBall("Wicket", 0);
  };

  // UNDO last ball
  const undo = () => {
    if (history.length === 0) return;

    const last = history[history.length - 1];
    setHistory(history.slice(0, -1));

    if (last.action === "Wicket") {
      setWickets(wickets - 1);
      setBalls(balls - 1);
    } else {
      setRuns(runs - last.run);
      if (last.action !== "Extra +" + last.run) {
        setBalls(balls - 1);
      }
    }
  };

  // Finish match
  const finishMatch = () => {
    alert(`Match Finished!\nFinal Score: ${runs}/${wickets} in ${overs} overs`);
  };

  // Reset Match
  const resetMatch = () => {
    setRuns(0);
    setWickets(0);
    setBalls(0);
    setHistory([]);
    setMatchStarted(false);
  };

  return (
    <div className={dark ? "bg-dark text-white min-vh-100 p-4" : "bg-light text-dark min-vh-100 p-4"}>
      <div className="container text-center">

        <button className="btn btn-outline-info float-end mb-3"
          onClick={() => setDark(!dark)}
        >
          {dark ? "Light Mode" : "Dark Mode"}
        </button>

        {!matchStarted && (
          <div className="card p-4 shadow mb-4">
            <h3>Select Total Overs</h3>
            <input
              type="number"
              className="form-control mb-3"
              value={totalOvers}
              onChange={e => setTotalOvers(Number(e.target.value))}
              min="1"
            />
            <button className="btn btn-success" onClick={() => setMatchStarted(true)}>Start Match</button>
          </div>
        )}

        {matchStarted && (
          <>
            <h1 className="fw-bold mb-4">Cricket Score Calculator</h1>

            <div className="card shadow p-4 mb-4">
              <h2 className="display-5">{runs} / {wickets}</h2>
              <p className="fs-4">Overs: {overs} / {totalOvers}</p>
            </div>

            <h4>Add / Remove Runs</h4>
            <div className="btn-group mb-4">
              <button
                className="btn btn-dark mx-1"
                onClick={() => setRuns(runs > 0 ? runs - 1 : 0)}
              >
                -1
              </button>

              {[1, 2, 3, 4, 6].map(n => (
                <button key={n} className="btn btn-primary mx-1" onClick={() => addRun(n)}>
                  +{n}
                </button>
              ))}
            </div>

            <h4>Extras</h4>
            <div className="btn-group mb-4">
              <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>Wide +1</button>
              <button className="btn btn-warning mx-1" onClick={() => addExtra(1)}>No Ball +1</button>
            </div>

            <div className="mb-4">
              <button className="btn btn-danger btn-lg mx-2" onClick={fallWicket}>Wicket</button>
              <button className="btn btn-secondary btn-lg mx-2" onClick={undo}>Undo</button>
            </div>

            <button className="btn btn-success mx-2" onClick={finishMatch}>Finish Match</button>
            <button className="btn btn-outline-secondary mx-2" onClick={resetMatch}>Reset</button>

            <hr />

            <h3>Over Summary</h3>
            <div className="table-responsive">
              <table className="table table-bordered table-striped table-sm">
                <thead>
                  <tr>
                    <th>Ball</th>
                    <th>Action</th>
                    <th>Runs</th>
                    <th>Over</th>
                  </tr>
                </thead>
                <tbody>
                  {history.map((h, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{h.action}</td>
                      <td>{h.run}</td>
                      <td>{h.over}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
